mapper.select:
SELECT TAB.ID,TAB.USERNAME,TAB.PASSWORD,TAB.STATUS,USERTYPE,TAB.MEMO
	FROM pptpd_admin TAB

		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}

ORDER BY TAB.ID DESC

mapper.update:
UPDATE pptpd_admin TAB
TAB.USERNAME = #{username}, TAB.STATUS = #{status}, TAB.USERTYPE = #{usertype},
WHERE TAB.ID = #{id}

mapper.select:
SELECT TAB.ID,TAB.USERNAME,TAB.PASSWORD,TAB.STATUS,USERTYPE,TAB.MEMO
	FROM pptpd_admin TAB

		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}

 ORDER BY TAB.ID DESC
 LIMIT #{pageStart}, #{pageSize}

SELECT COUNT(1) 
	FROM pptpd_admin TAB

		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}
