mapper.select:
SELECT TAB.ID,TAB.MGRID,TAB.USERNAME,TAB.PASSWORD,TAB.STATUS,TAB.CLIENTIP,TAB.SERVERIP,TAB.STARTDATE,TAB.ENDDATE,TAB.MEMO
	FROM pptpd_user TAB

		AND TAB.MGRID = #{mgrid}
		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}
		AND TAB.CLIENTIP = #{clientip}
		AND TAB.SERVERIP = #{serverip}
		AND TAB.STARTDATE = #{startdate}
		AND TAB.ENDDATE = #{enddate}

ORDER BY TAB.ID DESC

mapper.update:
UPDATE pptpd_user TAB
TAB.MGRID = #{mgrid}, TAB.USERNAME = #{username}, TAB.STATUS = #{status}, TAB.USERTYPE = #{usertype}, TAB.CLIENTIP = #{clientip}, TAB.SERVERIP = #{serverip}, TAB.STARTDATE = #{startdate}, TAB.ENDDATE = #{enddate},
WHERE TAB.ID = #{id}

mapper.select:
SELECT TAB.ID,TAB.MGRID,TAB.USERNAME,TAB.PASSWORD,TAB.STATUS,TAB.CLIENTIP,TAB.SERVERIP,TAB.STARTDATE,TAB.ENDDATE,TAB.MEMO
	FROM pptpd_user TAB
	LEFT JOIN pptpd_admin PA ON 

		AND TAB.MGRID = #{mgrid}
		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}
		AND TAB.CLIENTIP = #{clientip}
		AND TAB.SERVERIP = #{serverip}
		AND TAB.STARTDATE = #{startdate}
		AND TAB.ENDDATE = #{enddate}

 ORDER BY TAB.ID DESC
 LIMIT #{pageStart}, #{pageSize}

SELECT COUNT(1) 
	FROM pptpd_user TAB

		AND TAB.MGRID = #{mgrid}
		AND TAB.USERNAME = #{username}
		AND TAB.STATUS = #{status}
		AND TAB.USERTYPE = #{usertype}
		AND TAB.CLIENTIP = #{clientip}
		AND TAB.SERVERIP = #{serverip}
		AND TAB.STARTDATE = #{startdate}
		AND TAB.ENDDATE = #{enddate}
